import * as React from 'react';
import { CancelSmallMinor } from '@shopify/polaris-icons';
import { classNames } from '@shopify/css-utilities';
import { withAppProvider } from '../AppProvider';
import Icon from '../Icon';
import { handleMouseUpByBlurring } from '../../utilities/focus';
import styles from './Tag.scss';
function Tag({ children, disabled = false, onRemove, polaris: { intl }, }) {
    const className = classNames(disabled && styles.disabled, styles.Tag);
    const ariaLabel = intl.translate('Polaris.Tag.ariaLabel', { children });
    return (<span className={className}>
      <span title={children} className={styles.TagText}>
        {children}
      </span>
      <button type="button" aria-label={ariaLabel} className={styles.Button} onClick={onRemove} onMouseUp={handleMouseUpByBlurring} disabled={disabled}>
        <Icon source={CancelSmallMinor}/>
      </button>
    </span>);
}
export default withAppProvider()(Tag);
